mapper.select:
select

from advertisement_machine t
where t.id = #{id}

select

from advertisement_machine t


        t.userID = #{userID}


        and t.name like concat('%',#{name},'%')


        and t.address like concat('%',#{address},'%')


        and t.addTime between #{addTime} and concat(left(#{addTime}, 10), ' 23:59:59')


        and t.codeNumber = #{codeNumber}


        and t.remark like concat('%',#{remark},'%')


        and t.longitude = #{longitude}


        and t.latitude = #{latitude}


        and t.cameraIP = #{'%',#{cameraIP},'%'}


        and t.cameraSequence = #{'%',#{cameraSequence},'%'}


        and t.cameraVerificationCode = #{'%',#{cameraVerificationCode},'%'}


        and t.accessToken = #{'%',#{accessToken},'%'}




        order by t.addTime, convert(t.name using gbk) asc

select

from advertisement_machine t
where userID = #{userID}

select

from advertisement_machine t
where codeNumber = #{codeNumber}

mapper.update:
update advertisement_machine
set
longitude = #{longitude},
latitude = #{latitude}
where id = #{id}

update advertisement_machine
set light = #{operate}
where id = #{id}

update advertisement_machine
set charge = #{operate}
where id = #{id}

update advertisement_machine
set checked = #{operate}
where id = #{id}
